mapper.select:
select *
from (select rownum rnum, v.id, v.reg_idx, v.price, v.item_name, v.rest_num, v.deal_loc, v.link from v_select_purchase_main v



		where v.grade = #{grade}


		where v.deal_loc in

				#{list}



		where v.item_name like '%'||#{keyword}||'%'


		where v.grade = #{grade} and v.deal_loc in

				#{list}



		where v.deal_loc in

				#{list}
			 and v.item_name like '%'||#{keyword}||'%'


		where v.grade = #{grade} and v.item_name like '%'||#{keyword}||'%'


		where v.grade = #{grade} and v.item_name like '%'||#{keyword}||'%'
		and v.deal_loc in

				#{list}



)
where rnum between #{paging.start} and #{paging.end}


select count(*) from v_select_purchase_main



		where grade = #{grade}


		where deal_loc in

				#{list}



		where item_name like '%'||#{keyword}||'%'


		where grade = #{grade} and deal_loc in

				#{list}



		where deal_loc in

				#{list}
			 and item_name like '%'||#{keyword}||'%'


		where grade = #{grade} and item_name like '%'||#{keyword}||'%'


		where grade = #{grade} and item_name like '%'||#{keyword}||'%'
		and deal_loc in

				#{list}

select count(*) from purchase_register


	PR left join purchase_match PM using(reg_idx) left join purchase_join PJ using(join_idx) where (PR.id = #{id} OR PJ.id = #{id}) and ongoing = #{ongoing}


	where id = #{id} and done = #{done}


	PR left join purchase_match PM using(reg_idx) left join purchase_join PJ using(join_idx) where (PR.id = #{id} OR PJ.id = #{id})

select count(join_idx) 
FROM v_select_purchase_main v left join purchase_match using (reg_idx) 


		where v.grade = #{grade}


		where v.deal_loc in

				#{list}



		where v.item_name like '%'||#{keyword}||'%'


		where v.grade = #{grade} and v.deal_loc in

				#{list}



		where v.deal_loc in

				#{list}
			 and v.item_name like '%'||#{keyword}||'%'


		where v.grade = #{grade} and v.item_name like '%'||#{keyword}||'%'


		where v.grade = #{grade} and v.item_name like '%'||#{keyword}||'%'
		and v.deal_loc in

				#{list}




group by reg_idx order by reg_idx desc
